{
    "request": {
        "defaults": {
            "prompt": "My name is Olivier and I",
            "numResults": 1,
            "topKReturn": 1,
            "maxTokens": 4096,
            "minTokens": 0,
            "stopSequences": [],
            "temperature": 1.0,
            "topP": 1.0,
            "presencePenalty": {
                "scale": 0,
                "applyToWhitespaces": false,
                "applyToNumbers": false,
                "applyToEmojis": false,
                "applyToPunctuations": false,
                "applyToStopwords": false
            },
            "countPenalty": {
                "scale": 0,
                "applyToWhitespaces": false,
                "applyToNumbers": false,
                "applyToEmojis": false,
                "applyToPunctuations": false,
                "applyToStopwords": false
            },
            "frequencyPenalty": {
                "scale": 0,
                "applyToWhitespaces": false,
                "applyToNumbers": false,
                "applyToEmojis": false,
                "applyToPunctuations": false,
                "applyToStopwords": false
            }
        },
        "mapping": {
            "prompt": "$.prompt",
            "best_of": "$.numResults",
            "top_k": "$.topKReturn",
            "max_new_tokens": "$.maxTokens",
            "min_new_tokens": "$.minTokens",
            "temperature": "$.temperature",
            "top_p": "$.topP",
            "stop": "$.stopSequences"            
        }
    },
    "response": {
        "regex_sub": "",
        "mapping": {
            "generated_text": "$.completions[0].data.text",
            "finish_reason": "$.completions[0].finishReason"
        }
    },
    "response-with-stream": {
        "regex_sub": "",
        "mapping": {
            "generated_text": "$.completions[0].data.text",
            "finish_reason": "$.completions[0].finishReason"
        }
    }    
}

{
    "request": {
        "defaults": {
            "prompt": "My name is Olivier and I",
            "k": 1,
            "max_tokens": 4096,
            "stop_sequences": [],
            "temperature": 1.0,
            "p": 1.0,
            "return_likelihoods": "GENERATION",
            "stream": false
        },
        "mapping": {
            "prompt": "$.prompt",
            "top_k": "$.k",
            "max_new_tokens": "$.max_tokens",
            "temperature": "$.temperature",
            "top_p": "$.p",
            "stop": "$.stop_sequences",
            "stream": "$.stream"
        }
    },
    "response": {
        "regex_sub": "",
        "mapping": {
            "generated_text": "$.generations[0].text"
        }
    },
    "response-with-stream": {
        "regex_sub": "",
        "mapping": {
            "generated_text": "$.text",
            "finish_reason": "$.finish_reason"
        }
    }    
}

{
    "request": {
        "defaults": {
            "inputs": "My name is Olivier and I",
            "parameters": {
                "max_new_tokens": 1024,
                "return_full_text": false,
                "temperature": 0.3,
                "top_k": 1,
                "top_p": 0.3
            }
        },
        "mapping": {
            "prompt": "$.inputs",
            "stream": "$.stream",
            "max_new_tokens": "$.parameters.max_new_tokens",
            "temperature": "$.parameters.temperature",
            "top_p": "$.parameters.top_p",
            "top_k": "$.parameters.top_k"
        }
    },
    "response": {
        "regex_sub": "",
        "mapping": {
            "generated_text": "$.[0].generation"
        }
    },
    "response-with-stream": {
        "regex_sub": "^data:",
        "mapping": {
            "text": "$.token.text",
            "logprobs": "$.token.logprob",
            "finish_reason": "$.details.finish_reason",
            "generated_tokens": "$.details.generated_tokens",
            "seed": "$.details.seed",
            "generated_text": "$.token.text",
            "id": "$.token.id",
            "special": "$.token.special"
        }
    }
}
